mentById('email1').value.match(/^\w+(['\.\-\+]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,})+$/) == null){		  alert('Not a valid email address');		}	}	if(document.getElementById('email2') && document.getElementById('email2').value.length >0) {		if(document.getElementById('email2').value.match(/^\w+(['\.\-\+]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,})+$/) == null){		  alert('Not a valid email address');		}	}}$(document).ready(function() {     parent.$.colorbox.resize({        innerWidth:$('body').width(),        innerHeight:$('body').height()+14    });	document.getElementById('reqID').value = makeid();});	</script></body></html>